import { Button, Card, Row, Col } from 'react-bootstrap';

function StackP({ pSendFunction, isFlag, item }) {
    const { handleEdit } = pSendFunction;
    const { isEditable, isToggle, isEdit } = isFlag;

    return (
        <Card style={{ backgroundColor: '#ffffff', height: '80px' }} className='text-center mb-3'>
            <Row className='ms-3 mt-3'>
                <Col xs={10}>
                    <Row>{item.stackName}</Row>
                    <Row>{item.stackDescription}</Row>
                </Col>
                <Col xs={2}>
                    {isEditable && (
                        <Button
                            variant='outline-info'
                            onClick={() => handleEdit(item._id)}
                            disabled={isToggle || isEdit ? true : false}>
                            편집
                        </Button>
                    )}
                </Col>
            </Row>
        </Card>
    );
}

export default StackP;
