import { useNavigate } from 'react-router-dom';
import { Card, Row, Button, Col } from 'react-bootstrap';

function UserCard({ user, setIsEditing, isEditable, isNetwork, userImageUrl }) {
    const navigate = useNavigate();

    if (userImageUrl === undefined) {
        userImageUrl = 'http://placekitten.com/200/200';
    }

    return (
        <Card className='mb-2 ms-3 mr-5' style={{ width: '18rem' }}>
            <Card.Body>
                <Row className='justify-content-md-center'>
                    <Card.Img
                        style={{ width: '10rem', height: '8rem', borderRadius: '70%', overflow: 'hidden' }}
                        className='mb-3'
                        // src='http://placekitten.com/200/200'
                        src={userImageUrl}
                        alt='랜덤 고양이 사진 (http://placekitten.com API 사용)'
                    />
                </Row>
                <Card.Title>{user?.name}</Card.Title>
                <Card.Subtitle className='mb-2 text-muted'>{user?.email}</Card.Subtitle>
                <Card.Text className='mb-2 text-muted'>{user?.description}</Card.Text>
                {(!user?.gitLink || user?.gitLink !== 'undefined') && (
                    <Card.Link href={`https://${user?.gitLink}`}>{user?.gitLink}</Card.Link>
                )}

                {isEditable && (
                    <Col>
                        <Row className='mt-3 text-center text-info'>
                            <Col sm={{ span: 20 }}>
                                <Button variant='outline-info' size='sm' onClick={() => setIsEditing(true)}>
                                    편집
                                </Button>
                            </Col>
                        </Row>
                    </Col>
                )}

                {isNetwork && (
                    <Row>
                        <Card.Link className='mt-3' href='#' onClick={() => navigate(`/user/${user._id}`)}>
                            포트폴리오
                        </Card.Link>
                    </Row>
                )}
            </Card.Body>
        </Card>
    );
}

export default UserCard;
