import React, { useEffect, useContext, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { Container, Row, Col, Card, Form, Button } from 'react-bootstrap';

import * as Api from '../../api';
import { UserStateContext } from '../../App';

function WantedUpdate() {
    const navigate = useNavigate();
    const [wantedComment, setWantedComment] = useState('');
    const [writerName, setWriterName] = useState('');
    const [writerImage, setWriterImage] = useState('');

    const userState = useContext(UserStateContext);
    const { state } = useLocation();
    const { wanted } = state;

    const onChangeComment = (e) => {
        setWantedComment(e.target.value);
    };

    const fetchWriterInfo = async () => {
        try {
            const userId = wanted.userId;
            // "/user/user_id" 엔드포인트로 put요청함.(userId는 req.currentUserId 사용)
            const res = await Api.get(`users/${userId}`);
            const writerInfo = res.data.userInfo;
            setWriterName(writerInfo.name);

            if (!writerInfo.userImage.imageUri) {
                setWriterImage('http://placekitten.com/200/200');
            } else {
                setWriterImage(writerInfo.userImage.imageUri);
            }
        } catch (err) {
            console.log('작성자 불러오기에 실패하였습니다.', err);
        }
    };

    useEffect(() => {
        // 만약 전역 상태의 user가 null이라면, 로그인 페이지로 이동함.
        if (!userState.user) {
            navigate('/login');
            return;
        }

        fetchWriterInfo();
    }, [userState, navigate]);

    return (
        <Container>
            <Row>
                <Col xs={8} className='text-left'>
                    <h3>팀원 구하기</h3>
                    <p>
                        팀원 모집을 확인하고 프로젝트에 참여해보세요.
                        <br />
                        원하는 프로젝트가 없을 경우 직접 팀원을 모집할 수 있어요.
                    </p>
                </Col>
                <Col className='position-relative'>
                    <Button
                        className='m-3 position-absolute bottom-0 end-0'
                        variant='primary'
                        onClick={() => navigate('/wanted')}>
                        목록으로
                    </Button>
                </Col>
            </Row>
            <Row>
                <Card className='mb-3'>
                    <Card.Body>
                        <Card.Title>{wanted.wantedTitle}</Card.Title>
                        <Card.Subtitle>
                            작성자 : {writerName} &nbsp;&nbsp;&nbsp;&nbsp; 작성일시 : {wanted.createdAt}
                        </Card.Subtitle>
                        <hr />
                    </Card.Body>
                    <Card.Body>
                        <Card.Text>{wanted.wantedContent}</Card.Text>
                    </Card.Body>
                    <Card.Body>
                        <Card.Img
                            style={{ width: '10rem', height: '8rem' }}
                            className='mt-10'
                            src={writerImage}
                            alt='페이지 작성자'
                        />
                        <Card.Subritle>13개의 댓글</Card.Subritle>
                    </Card.Body>
                </Card>
            </Row>
        </Container>
    );
}

export default WantedUpdate;
