import React, { useEffect, useContext, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Container, Row, Col } from 'react-bootstrap';

import * as Api from '../../api';
import UserCard from '../../components/user/UserCard';
import { UserStateContext } from '../../App';

function Network() {
    const navigate = useNavigate();
    const userState = useContext(UserStateContext);
    // useState 훅을 통해 users 상태를 생성함.
    const [wantedList, setWantedList] = useState([]);
    // useState 훅을 통해 userImageUrl 상태를 생성함.

    useEffect(() => {
        // 만약 전역 상태의 user가 null이라면, 로그인 페이지로 이동함.
        if (!userState.user) {
            navigate('/login');
            return;
        }
        // "wantedlist" 엔드포인트로 GET 요청을 하고, wantedList response의 data로 세팅함.
        // Api.get('wantedlist').then((res) => setWantedList(res.data));
    }, [userState, navigate]);

    return (
        <Container>
            <Row xs='auto' sm={12} className='jusify-content-center'>
                <Col sm={12} className='text-left'>
                    <h3 className='m-3'>팀원 구하기</h3>
                    <p className='m-3'>
                        팀원 모집을 확인하고 프로젝트에 참여해보세요.
                        <br />
                        원하는 프로젝트가 없을 경우 직접 팀원을 모집할 수 있어요.
                    </p>
                </Col>
            </Row>
            <Row>
                {wantedList.map((user) => (
                    <React.Fragment key={user.id}>
                        {user.userImage ? (
                            <UserCard user={user} isNetwork userImageUrl={user.userImage.imageUri} />
                        ) : (
                            <UserCard user={user} isNetwork userImageUrl={'http://placekitten.com/200/200'} />
                        )}
                    </React.Fragment>
                    // <UserCard key={user.id} user={user} isNetwork />
                ))}
            </Row>
        </Container>
    );
}

export default Network;
