import React, { useState, useEffect } from 'react';
import UserEditForm from './UserEditForm';
import UserCard from './UserCard';
import * as Api from '../../api';

function User({ portfolioOwnerId, isEditable }) {
    // useState 훅을 통해 isEditing 상태를 생성함.
    const [isEditing, setIsEditing] = useState(false);
    // useState 훅을 통해 user 상태를 생성함.
    const [user, setUser] = useState(null);

    const [imageUrl, setImageUrl] = useState('');

    useEffect(() => {
        // "users/유저id" 엔드포인트로 GET 요청을 하고, user를 response의 data로 세팅함.
        Api.get('users', portfolioOwnerId).then((res) => setUser(res.data));

        Api.get(`users/image`)
            .then((res) => setImageUrl(URL.createObjectURL(res.data)))
            .catch((err) => console.error(err));
    }, [portfolioOwnerId]);

    return (
        <>
            {isEditing ? (
                <UserEditForm user={user} setIsEditing={setIsEditing} setUser={setUser} />
            ) : (
                <UserCard user={user} setIsEditing={setIsEditing} isEditable={isEditable} userImageUrl={imageUrl} />
            )}
        </>
    );
}

export default User;
