import React, { useState, useEffect } from 'react';
import UserEditForm from './UserEditForm';
import UserCard from './UserCard';
import * as Api from '../../api';

function User({ portfolioOwnerId, isEditable }) {
    // useState 훅을 통해 isEditing 상태를 생성함.
    const [isEditing, setIsEditing] = useState(false);
    // useState 훅을 통해 user 상태를 생성함.
    const [user, setUser] = useState(null);
    const [userImageUrl, setUserImageUrl] = useState('');

    useEffect(() => {
        // "users/유저id" 엔드포인트로 GET 요청을 하고, user를 response의 data로 세팅함.
        // Api.get('users', portfolioOwnerId).then((res) => console.log(res));
        // Api.get('users', portfolioOwnerId).then((res) => {
        // setUser(res.data.userInfo);
        // setUserImageUrl(res.data.imagePath);
        // });

        const fetchUser = async () => {
            try {
                const response = await Api.get('users', portfolioOwnerId);
                setUser(response.data.userInfo);
                setUserImageUrl(response.data.imagePath);
            } catch (error) {
                console.error(error);
            }
        };
        fetchUser();
    }, [portfolioOwnerId]);

    return (
        <>
            {isEditing ? (
                <UserEditForm user={user} setIsEditing={setIsEditing} setUser={setUser} />
            ) : (
                <UserCard user={user} setIsEditing={setIsEditing} isEditable={isEditable} userImageUrl={userImageUrl} />
            )}
        </>
    );
}

export default User;
